    Locations calMovement(float RPY[3], float XYZ[3]){
        float a,b,c;
        a=RPY[2]*M_PI/360.0;
        b=RPY[1]*M_PI/360.0;
        c=RPY[0]*M_PI/360.0;
        
        Eigen::Matrix4f R;
        R<<  cos(a)*cos(b), cos(a)*sin(b)*sin(c)-sin(a)*cos(c), cos(a)*sin(b)*cos(c)+sin(a)*sin(c),0,
             sin(a)*cos(b), sin(a)*sin(b)*sin(c)+cos(a)*cos(c), sin(a)*sin(b)*cos(c)-cos(a)*sin(c),0,
             -sin(b)      , cos(b)*sin(c)                     , cos(b)*cos(c)                     ,0,
             0            , 0                                 ,0                                  ,1;

        Eigen::MatrixXf Pin1(4,1);
        Pin1(0,0) = P1X-XYZ[0];
        Pin1(1,0) = P1Y-XYZ[1];
        Pin1(2,0) = P1Z-XYZ[2];
        Pin1(3,0) = 1.0;

        Eigen::MatrixXf Pin2(4,1);
        Pin2(0,0) = P2X-XYZ[0];
        Pin2(1,0) = P2Y-XYZ[1];
        Pin2(2,0) = P2Z-XYZ[2];
        Pin2(3,0) = 1.0;

        Eigen::MatrixXf Pin3(4,1);
        Pin3(0,0) = P3X-XYZ[0];
        Pin3(1,0) = P3Y-XYZ[1];
        Pin3(2,0) = P3Z-XYZ[2];
        Pin3(3,0) = 1.0;

        Eigen::MatrixXf Pin4(4,1);
        Pin4(0,0) = P4X-XYZ[0];
        Pin4(1,0) = P4Y-XYZ[1];
        Pin4(2,0) = P4Z-XYZ[2];
        Pin4(3,0) = 1.0;

        Eigen::MatrixXf Pin5(4,1);
        Pin5(0,0) = P5X-XYZ[0];
        Pin5(1,0) = P5Y-XYZ[1];
        Pin5(2,0) = P5Z-XYZ[2];
        Pin5(3,0) = 1.0;

        Eigen::MatrixXf Pin6(4,1);
        Pin6(0,0) = P6X-XYZ[0];
        Pin6(1,0) = P6Y-XYZ[1];
        Pin6(2,0) = P6Z-XYZ[2];
        Pin6(3,0) = 1.0;

        Eigen::MatrixXf Pout1(4,1);
        Pout1=R*Pin1;
        Eigen::MatrixXf Pout2(4,1);
        Pout2=R*Pin2;
        Eigen::MatrixXf Pout3(4,1);
        Pout3=R*Pin3;
        Eigen::MatrixXf Pout4(4,1);
        Pout4=R*Pin4;
        Eigen::MatrixXf Pout5(4,1);
        Pout5=R*Pin5;
        Eigen::MatrixXf Pout6(4,1);
        Pout6=R*Pin6;
        
        hexapod::Point3D foreRight(Pout1(0,0),Pout1(1,0),Pout1(2,0));
        hexapod::Point3D right(Pout2(0,0),Pout2(1,0),Pout2(2,0));
        hexapod::Point3D hindRight(Pout3(0,0),Pout3(1,0),Pout3(2,0));
        hexapod::Point3D hindLeft(Pout4(0,0),Pout4(1,0),Pout4(2,0));
        hexapod::Point3D left(Pout5(0,0),Pout5(1,0),Pout5(2,0));
        hexapod::Point3D foreLeft(Pout6(0,0),Pout6(1,0),Pout6(2,0));
  
        hexapod::Locations locations(foreRight,right,hindRight,hindLeft,left,foreLeft);
        return locations; 
    }



